import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import {IPais} from '../interfaces/pais.interface';

@Injectable({
  providedIn: 'root'
})
export class PaisesService {

  private paises: IPais[] = [];

  constructor(private http:HttpClient) { }

  getPaises(): Promise<IPais[]>{
    if(this.paises.length>0){
      return Promise.resolve(this.paises);
    }
    return new Promise( resolve=>{
      this.http.get('https://restcountries.com/v2/lang/es')
      .subscribe( (paises) => {
        console.log(paises);
        this.paises = <IPais[]>paises;
        resolve(<IPais[]>paises);
      })
    })
  }
}
